// Helper functions for parable access

/**
 * Get all parables
 */
function getAllParables() {
    return PARABLES_DATA.parables;
}

/**
 * Get a specific parable by ID
 */
function getParableById(id) {
    return PARABLES_DATA.parables.find(parable => parable.id === id);
}

/**
 * Get a parable by title (case insensitive)
 */
function getParableByTitle(title) {
    return PARABLES_DATA.parables.find(parable => 
        parable.title.toLowerCase() === title.toLowerCase()
    );
}

/**
 * Search parables by title (partial match, case insensitive)
 */
function searchParablesByTitle(searchTerm) {
    return PARABLES_DATA.parables.filter(parable =>
        parable.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
}

/**
 * Get all parables from a specific book
 */
function getParablesByBook(bookName) {
    return PARABLES_DATA.parables.filter(parable =>
        parable.accounts.some(account => account.book === bookName)
    );
}

/**
 * Get all parables from a specific book and chapter
 */
function getParablesByBookAndChapter(bookName, chapter) {
    return PARABLES_DATA.parables.filter(parable =>
        parable.accounts.some(account => 
            account.book === bookName && account.chapter === chapter
        )
    );
}

/**
 * Search parable text content (case insensitive)
 */
function searchParableText(searchTerm) {
    const results = [];
    
    PARABLES_DATA.parables.forEach(parable => {
        parable.accounts.forEach(account => {
            const matchingVerses = account.text.filter(verse =>
                verse.text.toLowerCase().includes(searchTerm.toLowerCase())
            );
            
            if (matchingVerses.length > 0) {
                results.push({
                    parable: {
                        id: parable.id,
                        title: parable.title
                    },
                    account: {
                        book: account.book,
                        chapter: account.chapter,
                        verses: account.verses
                    },
                    matchingVerses: matchingVerses
                });
            }
        });
    });
    
    return results;
}

/**
 * Get parables that appear in multiple Gospels (cross-references)
 */
function getCrossReferencedParables() {
    return PARABLES_DATA.parables.filter(parable => parable.accounts.length > 1);
}

/**
 * Get parables unique to a specific Gospel
 */
function getUniqueParables(bookName) {
    return PARABLES_DATA.parables.filter(parable =>
        parable.accounts.length === 1 && parable.accounts[0].book === bookName
    );
}

/**
 * Get total verse count for a parable
 */
function getParableVerseCount(parableId) {
    const parable = getParableById(parableId);
    if (!parable) return 0;
    
    return parable.accounts.reduce((total, account) => 
        total + account.text.length, 0
    );
}

/**
 * Get metadata about the parables collection
 */
function getParablesMetadata() {
    return PARABLES_DATA.metadata;
}

/**
 * Get statistics about the parables
 */
function getParablesStatistics() {
    const totalAccounts = PARABLES_DATA.parables.reduce((sum, parable) => 
        sum + parable.accounts.length, 0
    );
    
    const totalVerses = PARABLES_DATA.parables.reduce((sum, parable) => 
        sum + parable.accounts.reduce((accSum, account) => 
            accSum + account.text.length, 0
        ), 0
    );
    
    const bookCounts = {};
    PARABLES_DATA.parables.forEach(parable => {
        parable.accounts.forEach(account => {
            bookCounts[account.book] = (bookCounts[account.book] || 0) + 1;
        });
    });
    
    return {
        totalParables: PARABLES_DATA.parables.length,
        totalAccounts: totalAccounts,
        totalVerses: totalVerses,
        crossReferencedParables: getCrossReferencedParables().length,
        accountsByBook: bookCounts
    };
}

// Export for Node.js usage
if (typeof module !== 'undefined' && module.exports) {
    module.exports = {
        PARABLES_DATA,
        getAllParables,
        getParableById,
        getParableByTitle,
        searchParablesByTitle,
        getParablesByBook,
        getParablesByBookAndChapter,
        searchParableText,
        getCrossReferencedParables,
        getUniqueParables,
        getParableVerseCount,
        getParablesMetadata,
        getParablesStatistics
    };
}